#include "FB_test2.h"
#include <sys/time.h>

#ifndef MAX_TICKS
#define MAX_TICKS 100
#endif

//put a copy of the top level block into global memory
test2_t mytest2;

int main() {
	//printf("\n\n\n");
	//printf("\nTop: %20s   Size: %lu\n", "test2", sizeof(mytest2));

	#ifdef PRINT_VALS
	printf("Simulation time,");
	#endif

	if(test2_preinit(&mytest2) != 0) {
		printf("Failed to preinitialize.");
		return 1;
	}
	if(test2_init(&mytest2) != 0) {
		printf("Failed to initialize.");
		return 1;
	}
	printf("\n");
	
	struct timeval tv1, tv2;
	gettimeofday(&tv1, NULL);

	int tickNum = 1;
	do {
		#ifdef PRINT_VALS
			printf("%f,",(double)tickNum*0.01);
		#endif
		test2_syncOutputEvents(&mytest2);
		test2_syncInputEvents(&mytest2);

		test2_syncOutputData(&mytest2);
		test2_syncInputData(&mytest2);
		
		test2_run(&mytest2);
		#ifdef PRINT_VALS
			printf("\n");
		#endif
	} while(tickNum++ < MAX_TICKS);
	gettimeofday(&tv2, NULL);
	#ifdef PRINT_TIME
	printf ("Total time = %f seconds\n",
         (double) (tv2.tv_usec - tv1.tv_usec) / 1000000 +
         (double) (tv2.tv_sec - tv1.tv_sec));
	#endif
	return 0;
}

