//This is the main file for the iec61499 network with Netw as the top level block

#include "FB_Netw.h"
#include <sys/time.h>

#ifndef MAX_TICKS
#define MAX_TICKS 100
#endif

//put a copy of the top level block into global memory
Netw_t myNetw;

int main() {
	//printf("\n\n\n");
	//printf("\nTop: %20s   Size: %lu\n", "Netw", sizeof(myNetw));

	#ifdef PRINT_VALS
	printf("Simulation time,");
	#endif

	if(Netw_preinit(&myNetw) != 0) {
		printf("Failed to preinitialize.");
		return 1;
	}
	if(Netw_init(&myNetw) != 0) {
		printf("Failed to initialize.");
		return 1;
	}
	printf("\n");
	
	struct timeval tv1, tv2;
	gettimeofday(&tv1, NULL);

	int tickNum = 1;
	do {
		#ifdef PRINT_VALS
			printf("%f,",(double)tickNum*0.01);
		#endif
		Netw_syncOutputEvents(&myNetw);
		Netw_syncInputEvents(&myNetw);

		Netw_syncOutputData(&myNetw);
		Netw_syncInputData(&myNetw);
		
		Netw_run(&myNetw);
		#ifdef PRINT_VALS
			printf("\n");
		#endif
	} while(tickNum++ < MAX_TICKS);
	gettimeofday(&tv2, NULL);
	#ifdef PRINT_TIME
	printf ("Total time = %f seconds\n",
         (double) (tv2.tv_usec - tv1.tv_usec) / 1000000 +
         (double) (tv2.tv_sec - tv1.tv_sec));
	#endif
	return 0;
}

