//This is the main file for the iec61499 network with top as the top level block

#include "FB_top.h"
#include <sys/time.h>

//put a copy of the top level block into global memory
top_t mytop;

int main() {
	//printf("\n\n\n");
	//printf("\nTop: %20s   Size: %lu\n", "top", sizeof(mytop));

	#ifdef PRINT_VALS
	printf("Simulation time,");
	#endif

	if(top_preinit(&mytop) != 0) {
		printf("Failed to preinitialize.");
		return 1;
	}
	if(top_init(&mytop) != 0) {
		printf("Failed to initialize.");
		return 1;
	}
	printf("\n");
	
	struct timeval tv1, tv2;
	gettimeofday(&tv1, NULL);

	int tickNum = 1;
	do {
		#ifdef PRINT_VALS
			printf("%f,",(double)tickNum*0.01);
		#endif
		top_syncOutputEvents(&mytop);
		top_syncInputEvents(&mytop);

		top_syncOutputData(&mytop);
		top_syncInputData(&mytop);
		
		top_run(&mytop);
		#ifdef PRINT_VALS
			printf("\n");
		#endif
	} while(tickNum++ < MAX_TICKS);
	gettimeofday(&tv2, NULL);
	#ifdef PRINT_TIME
	printf ("Total time = %f seconds\n",
         (double) (tv2.tv_usec - tv1.tv_usec) / 1000000 +
         (double) (tv2.tv_sec - tv1.tv_sec));
	#endif
	return 0;
}

