//This is the main file for the iec61499 network with TrainGate as the top level block

#include "FB_TrainGate.h"
#include <sys/time.h>

//put a copy of the top level block into global memory
TrainGate_t myTrainGate;

int main() {
	//printf("\n\n\n");
	//printf("\nTop: %20s   Size: %lu\n", "TrainGate", sizeof(myTrainGate));

	#ifdef PRINT_VALS
	printf("Simulation time,");
	#endif

	if(TrainGate_preinit(&myTrainGate) != 0) {
		printf("Failed to preinitialize.");
		return 1;
	}
	if(TrainGate_init(&myTrainGate) != 0) {
		printf("Failed to initialize.");
		return 1;
	}
	printf("\n");
	
	struct timeval tv1, tv2;
	gettimeofday(&tv1, NULL);

	int tickNum = 1;
	do {
		#ifdef PRINT_VALS
			printf("%f,",(double)tickNum*0.01);
		#endif
		TrainGate_syncOutputEvents(&myTrainGate);
		TrainGate_syncInputEvents(&myTrainGate);

		TrainGate_syncOutputData(&myTrainGate);
		TrainGate_syncInputData(&myTrainGate);
		
		TrainGate_run(&myTrainGate);
		#ifdef PRINT_VALS
			printf("\n");
		#endif
	} while(tickNum++ < MAX_TICKS);
	gettimeofday(&tv2, NULL);
	#ifdef PRINT_TIME
	printf ("Total time = %f seconds\n",
         (double) (tv2.tv_usec - tv1.tv_usec) / 1000000 +
         (double) (tv2.tv_sec - tv1.tv_sec));
	#endif
	return 0;
}

