/* Controller_run() executes a single tick of an
 * instance of Controller according to synchronous semantics.
 * Notice that it does NOT perform any I/O - synchronisation
 * will need to be done in the parent.
 * Also note that on the first run of this function, trigger will be set
 * to true, meaning that on the very first run no next state logic will occur.
 */
void Controller_run(Controller_t  *me) {
	//if there are output events, reset them
	
	me->outputEvents.event.HeatChange = 0;
	

	//next state logic
	if(me->_trigger == false) {
		switch(me->_state) {
		case STATE_Controller_Start:
			if(true) {
				me->_state = STATE_Controller_h1;
				me->_trigger = true;
			};
			break;
		case STATE_Controller_h1:
			if(me->inputEvents.event.TChange1 && (me->T1 == 100)) {
				me->_state = STATE_Controller_h2;
				me->_trigger = true;
			};
			break;
		case STATE_Controller_h2:
			if(me->inputEvents.event.TChange2 && (me->T2 == 100)) {
				me->_state = STATE_Controller_h1;
				me->_trigger = true;
			};
			break;
		
		}
	}

	//state output logic
	if(me->_trigger == true) {
		switch(me->_state) {
		case STATE_Controller_Start:
			break;

		case STATE_Controller_h1:
			Controller_clearheat(me);
			me->outputEvents.event.HeatChange = 1;
			Controller_heat1(me);
			break;

		case STATE_Controller_h2:
			Controller_clearheat(me);
			me->outputEvents.event.HeatChange = 1;
			Controller_heat2(me);
			break;

		
		}
	}

	me->_trigger = false;
}


