/* controller_run() executes a single tick of an
 * instance of controller according to synchronous semantics.
 * Notice that it does NOT perform any I/O - synchronisation
 * will need to be done in the parent.
 * Also note that on the first run of this function, trigger will be set
 * to true, meaning that on the very first run no next state logic will occur.
 */
void controller_run(controller_t  *me) {
	//if there are output events, reset them
	
	me->outputEvents.event.TURNON = 0;
	me->outputEvents.event.TURNOFF = 0;
	

	//next state logic
	if(me->_trigger == false) {
		switch(me->_state) {
		case STATE_controller_Start:
			if(true) {
				me->_state = STATE_controller_on;
				me->_trigger = true;
			};
			break;
		case STATE_controller_on:
			if(me->timer == me->maxTime) {
				me->_state = STATE_controller_off;
				me->_trigger = true;
			} else if(me->timer < me->maxTime) {
				me->_state = STATE_controller_on;
				me->_trigger = true;
			};
			break;
		case STATE_controller_off:
			if(me->timer == 0) {
				me->_state = STATE_controller_on;
				me->_trigger = true;
			} else if(me->timer > 0) {
				me->_state = STATE_controller_off;
				me->_trigger = true;
			};
			break;
		
		}
	}

	//state output logic
	if(me->_trigger == true) {
		switch(me->_state) {
		case STATE_controller_Start:
			break;

		case STATE_controller_on:
			controller_upcount(me);
			me->outputEvents.event.TURNON = 1;
			break;

		case STATE_controller_off:
			controller_downcount(me);
			me->outputEvents.event.TURNOFF = 1;
			break;

		
		}
	}

	me->_trigger = false;
}


