/* Manager_run() executes a single tick of an
 * instance of Manager according to synchronous semantics.
 * Notice that it does NOT perform any I/O - synchronisation
 * will need to be done in the parent.
 * Also note that on the first run of this function, trigger will be set
 * to true, meaning that on the very first run no next state logic will occur.
 */
void Manager_run(Manager_t  *me) {
	//if there are output events, reset them
	
	me->outputEvents.event.Zero = 0;
	me->outputEvents.event.Tick = 0;
	

	//next state logic
	if(me->_trigger == false) {
		switch(me->_state) {
		case STATE_Manager_Start:
			if(true) {
				me->_state = STATE_Manager_zero;
				me->_trigger = true;
				
			};
			break;
		case STATE_Manager_next_val:
			if(true) {
				me->_state = STATE_Manager_run;
				me->_trigger = true;
				
			};
			break;
		case STATE_Manager_run:
			if(me->TickCount >= me->TicksPerVal) {
				me->_state = STATE_Manager_next_val;
				me->_trigger = true;
				
			} else if(me->TickCount < me->TicksPerVal) {
				me->_state = STATE_Manager_run;
				me->_trigger = true;
				
			};
			break;
		case STATE_Manager_zero:
			if(true) {
				me->_state = STATE_Manager_next_val;
				me->_trigger = true;
				
			};
			break;
		
		}
	}

	//state output logic
	if(me->_trigger == true) {
		switch(me->_state) {
		case STATE_Manager_Start:
			break;

		case STATE_Manager_next_val:
			Manager_ManagerSetNextVal(me);
			break;

		case STATE_Manager_run:
			Manager_ManagerRun(me);
			me->outputEvents.event.Tick = 1;
			break;

		case STATE_Manager_zero:
			me->outputEvents.event.Zero = 1;
			break;

		
		}
	}

	me->_trigger = false;
}


