/* VVI_VRP_run() executes a single tick of an
 * instance of VVI_VRP according to synchronous semantics.
 * Notice that it does NOT perform any I/O - synchronisation
 * will need to be done in the parent.
 * Also note that on the first run of this function, trigger will be set
 * to true, meaning that on the very first run no next state logic will occur.
 */
void VVI_VRP_run(VVI_VRP_t  *me) {
	//if there are output events, reset them
	
	me->outputEvents.event.VRP_Start_Timer = 0;
	me->outputEvents.event.VSense = 0;
	me->outputEvents.event.VRefractory = 0;
	

	//next state logic
	if(me->_trigger == false) {
		switch(me->_state) {
		case STATE_VVI_VRP_Start:
			if(true) {
				me->_state = STATE_VVI_VRP_Detected_VPulse;
				me->_trigger = true;
				
			};
			break;
		case STATE_VVI_VRP_Wait_For_VSense:
			if(me->inputEvents.event.VPulse) {
				me->_state = STATE_VVI_VRP_Resting;
				me->_trigger = true;
				
			} else if(me->inputEvents.event.VPace) {
				me->_state = STATE_VVI_VRP_Resting;
				me->_trigger = true;
				
			};
			break;
		case STATE_VVI_VRP_Resting:
			if(me->inputEvents.event.VPulse) {
				me->_state = STATE_VVI_VRP_Detected_VRefractory;
				me->_trigger = true;
				
			} else if(me->inputEvents.event.VRP_Timer_Timeout) {
				me->_state = STATE_VVI_VRP_Wait_For_VSense;
				me->_trigger = true;
				
			};
			break;
		case STATE_VVI_VRP_Detected_VPulse:
			if(me->inputEvents.event.VRP_Timer_Timeout) {
				me->_state = STATE_VVI_VRP_Wait_For_VSense;
				me->_trigger = true;
				
			};
			break;
		case STATE_VVI_VRP_Detected_VRefractory:
			if(true) {
				me->_state = STATE_VVI_VRP_Resting;
				me->_trigger = true;
				
			};
			break;
		
		}
	}

	//state output logic
	if(me->_trigger == true) {
		switch(me->_state) {
		case STATE_VVI_VRP_Start:
			break;

		case STATE_VVI_VRP_Wait_For_VSense:
			break;

		case STATE_VVI_VRP_Resting:
			VVI_VRP_VRP_Set_Timeout_Value(me);
			me->outputEvents.event.VRP_Start_Timer = 1;
			me->outputEvents.event.VSense = 1;
			break;

		case STATE_VVI_VRP_Detected_VPulse:
			VVI_VRP_VRP_Set_Timeout_Value(me);
			me->outputEvents.event.VRP_Start_Timer = 1;
			break;

		case STATE_VVI_VRP_Detected_VRefractory:
			me->outputEvents.event.VRefractory = 1;
			break;

		
		}
	}

	me->_trigger = false;
}


